import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import Button from "react-bootstrap/Button";
import { Container, Row, Col } from "react-bootstrap";
import "./App.css";
const initial = {
  account: "",
  pancard: "",
  number: "",
  aadhaar: "",
  accountError: "",
  pancardError: "",
  aadhaarError: "",
  addressError: "",
};
class App extends Component {
  state = initial;
  handlechange = (event) => {
    const isCheckbox = event.target.type === "checkbox";
    this.setState({
      [event.target.name]: isCheckbox
        ? event.target.checked
        : event.target.value,
    });
  };

  validate = () => {
    let nameError = "";
    let emailError = "";
    let numberError = "";
    let addressError = "";

    if (!this.state.name) {
      nameError = "Name Cannot Be Blank";
    }
    if (!this.state.email.includes("@")) {
      emailError = "invalid Email";
    }
    if (!this.state.number) {
      numberError = "Number Cannot Be Blank";
    }
    if (!this.state.address) {
      addressError = "Address Cannot Be Blank";
    }
    if (emailError || nameError || numberError || addressError) {
      this.setState({ emailError, nameError, numberError, addressError });
      return false;
    }
    return true;
  };

  handleSubmit = (event) => {
    event.preventDefault();
    const isValid = this.validate();
    if (isValid) {
      console.log(this.state);
      this.setState(initial);
    }
  };
  render() {
    return (
      <Container fluid className="head">
        <Row>
          <Col md={12}>
            <div className="main">
              <h1>Get Your Loan Report & Score Now</h1>
              <h3>With Loan analysis and monthly updates</h3>
              <hr></hr>
              <input
                name="account"
                type="text"
                placeholder="Enter Account Number"
                value={this.state.accountError}
                onChange={this.handlechange}
              ></input>
              <input
                name="pancard"
                type="text"
                placeholder="Pan Card Number"
                maxLength="10"
                value={this.state.pancardError}
                onChange={this.handlechange}
              ></input>
              <input
                name="aadhaar"
                type="text"
                placeholder="Aadhaar Number"
                maxLength="12"
                value={this.state.aadhaarError}
                onChange={this.handlechange}
              ></input>
              <br></br>
              <button className="uploadbtn"> 3 Month Bank Receipt</button>
              <input type="file" className="upload"></input>
              <br></br>
              <Button type="submit" className="Button" onClick={this.handleSubmit}>
                Submit
              </Button>
            </div>
          </Col>
        </Row>
      </Container>
    );
  }
}

export default App;
